import React, { useState, useEffect } from "react";
import "./App.css";
import { motion } from "framer-motion";
import { FaCheckCircle, FaTimesCircle, FaExclamationTriangle } from "react-icons/fa";

function App() {
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [metrics, setMetrics] = useState({
    total: 0,
    approved: 0,
    blocked: 0,
    pending: 0,
  });

  const fetchTransactions = async () => {
    setLoading(true);
    try {
      const res = await fetch("http://localhost:3001/api/alerts");
      const data = await res.json();
      if (Array.isArray(data)) {
        setTransactions(data);
        calculateMetrics(data);
      } else {
        setTransactions([]);
      }
    } catch (err) {
      console.error("Error fetching data:", err);
    }
    setLoading(false);
  };

  const calculateMetrics = (data) => {
    setMetrics({
      total: data.length,
      approved: data.filter((t) => t.decision === "approve").length,
      blocked: data.filter((t) => t.decision === "block").length,
      pending: data.filter((t) => !t.decision || t.decision === "pending").length,
    });
  };

  const handleAction = async (id, action) => {
    try {
      await fetch("http://localhost:3001/api/action", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ alertId: id, action }),
      });
      fetchTransactions();
    } catch (error) {
      console.error("Error updating action:", error);
    }
  };

  useEffect(() => {
    fetchTransactions();
    const interval = setInterval(fetchTransactions, 10000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="dashboard">
      <header className="header">
        <h1>AI Fraud Detection Dashboard</h1>
        <p>Real-time monitoring and risk analytics</p>
      </header>

      <section className="metrics">
        <motion.div className="card total" whileHover={{ scale: 1.05 }}>
          <h2>Total Alerts</h2>
          <p>{metrics.total}</p>
        </motion.div>
        <motion.div className="card approved" whileHover={{ scale: 1.05 }}>
          <h2>Approved</h2>
          <p>{metrics.approved}</p>
        </motion.div>
        <motion.div className="card blocked" whileHover={{ scale: 1.05 }}>
          <h2>Blocked</h2>
          <p>{metrics.blocked}</p>
        </motion.div>
        <motion.div className="card pending" whileHover={{ scale: 1.05 }}>
          <h2>Pending</h2>
          <p>{metrics.pending}</p>
        </motion.div>
      </section>

      <section className="table-section">
        <h2>Transaction Alerts</h2>
        {loading ? (
          <p className="loading">Loading data...</p>
        ) : (
          <table className="transaction-table">
            <thead>
              <tr>
                <th>Account ID</th>
                <th>Amount ($)</th>
                <th>Risk Score</th>
                <th>Reason</th>
                <th>Status</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {transactions.map((t) => (
                <motion.tr
                  key={t._id}
                  initial={{ opacity: 0, y: 5 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.3 }}
                  className={
                    t.decision === "block"
                      ? "blocked-row"
                      : t.decision === "approve"
                      ? "approved-row"
                      : "pending-row"
                  }
                >
                  <td>{t.account_id}</td>
                  <td>${t.amount}</td>
                  <td>{(t.score * 100).toFixed(1)}%</td>
                  <td>{t.reason || "Unknown"}</td>
                  <td>
                    {t.decision === "block" ? (
                      <FaTimesCircle className="icon blocked" />
                    ) : t.decision === "approve" ? (
                      <FaCheckCircle className="icon approved" />
                    ) : (
                      <FaExclamationTriangle className="icon pending" />
                    )}
                  </td>
                  <td>
                    {t.decision === "approve" || t.decision === "block" ? (
                      <span className="done">Action Taken</span>
                    ) : (
                      <div className="btn-group">
                        <button
                          className="btn approve"
                          onClick={() => handleAction(t._id, "approve")}
                        >
                          Approve
                        </button>
                        <button
                          className="btn block"
                          onClick={() => handleAction(t._id, "block")}
                        >
                          Block
                        </button>
                      </div>
                    )}
                  </td>
                </motion.tr>
              ))}
            </tbody>
          </table>
        )}
      </section>
    </div>
  );
}

export default App;
