import React, { useEffect, useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [alerts, setAlerts] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchAlerts = async () => {
    try {
      const res = await axios.get("http://localhost:3001/api/alerts");
      setAlerts(res.data);
    } catch (err) {
      console.error("Error fetching alerts:", err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchAlerts();
  }, []);

  const handleDecision = async (id, action) => {
    alert(`Transaction ${id} marked as ${action.toUpperCase()}`);
  };

  return (
    <div className="App">
      <h1>ðŸ’³ Fraud Detection Analyst Dashboard</h1>

      {loading ? (
        <p>Loading alerts...</p>
      ) : alerts.length === 0 ? (
        <p>No suspicious transactions right now ðŸš€</p>
      ) : (
        <table>
          <thead>
            <tr>
              <th>Account ID</th>
              <th>Score</th>
              <th>Decision</th>
              <th>Reasons</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {alerts.map((a) => (
              <tr key={a._id}>
                <td>{a.account_id}</td>
                <td>{a.score.toFixed(2)}</td>
                <td className={a.decision === "block" ? "block" : "alert"}>
                  {a.decision.toUpperCase()}
                </td>
                <td>{a.reasons.join(", ")}</td>
                <td>
                  <button onClick={() => handleDecision(a._id, "approve")}>âœ… Approve</button>
                  <button onClick={() => handleDecision(a._id, "block")}>â›” Block</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      <button className="refresh" onClick={fetchAlerts}>ðŸ”„ Refresh Alerts</button>
    </div>
  );
}

export default App;
