import React, { useEffect, useState } from "react";
import axios from "axios";
import { motion } from "framer-motion";
import {
  PieChart,
  Pie,
  Tooltip,
  Cell,
  ResponsiveContainer,
  Legend,
} from "recharts";
import toast, { Toaster } from "react-hot-toast";
import "./App.css";

const COLORS = ["#facc15", "#22c55e", "#ef4444"]; // gold, green, red

export default function App() {
  const [alerts, setAlerts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [dark, setDark] = useState(true);
  const [error, setError] = useState(null);

  // Configure axios base URL to avoid hardcoding endpoints everywhere
  axios.defaults.baseURL = process.env.REACT_APP_BACKEND_URL || "http://localhost:3001";

  const fetchAlerts = async () => {
    setLoading(true);
    setError(null);
    try {
      const res = await axios.get("/api/alerts");
      setAlerts(Array.isArray(res.data) ? res.data : []);
    } catch (err) {
      console.error(err);
      setError(err.message || "Failed to fetch alerts");
      toast.error("Failed to fetch alerts");
      setAlerts([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchAlerts();
  }, []);

  const handleDecision = async (id, action) => {
    try {
      // Optimistic UI update
      setAlerts((prev) => prev.map((a) => (a._id === id ? { ...a, analyst_action: action } : a)));

      const res = await axios.post("/api/action", {
        alertId: id,
        action,
      });

      if (res.status >= 200 && res.status < 300) {
        toast.success(`Marked as ${action.toUpperCase()}`);
        // re-sync with server to get latest fields
        fetchAlerts();
      } else {
        throw new Error(`Action failed: ${res.status}`);
      }
    } catch (err) {
      console.error(err);
      toast.error("Error saving action");
      // keep UI in sync
      fetchAlerts();
    }
  };

  const approvedCount = alerts.filter((a) => a.analyst_action === "approve" || a.decision === "allow").length;
  const blockedCount = alerts.filter((a) => a.analyst_action === "block").length;
  const pendingCount = alerts.filter((a) => a.decision === "alert" && !a.analyst_action).length;

  const chartData = [
    { name: "Approved", value: approvedCount },
    { name: "Blocked", value: blockedCount },
    { name: "Pending", value: pendingCount },
  ];

  return (
    <div className={`app ${dark ? "dark" : ""}`}>
      <Toaster position="top-right" />
      <header className="navbar">
        <h1>üí≥ Real-Time Fraud Detection Console</h1>
        <button onClick={() => setDark(!dark)} className="mode-btn">
          {dark ? "‚òÄÔ∏è Light" : "üåô Dark"}
        </button>
      </header>

      <main className="main">
        <div className="cards">
          <motion.div whileHover={{ scale: 1.05 }} className="card blue">
            <h3>Total Alerts</h3>
            <p>{alerts.length}</p>
          </motion.div>
          <motion.div whileHover={{ scale: 1.05 }} className="card green">
            <h3>Approved</h3>
            <p>{alerts.filter((a) => a.analyst_action === "approve").length}</p>
          </motion.div>
          <motion.div whileHover={{ scale: 1.05 }} className="card red">
            <h3>Blocked</h3>
            <p>{alerts.filter((a) => a.analyst_action === "block").length}</p>
          </motion.div>
        </div>

        <div className="chart-container">
          <h2>Fraud Overview</h2>
          <ResponsiveContainer width="100%" height={250}>
            <PieChart>
              <Pie dataKey="value" data={chartData} outerRadius={90} fill="#8884d8" label>
                {chartData.map((entry, index) => (
                  <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                ))}
              </Pie>
              <Tooltip />
              <Legend />
            </PieChart>
          </ResponsiveContainer>
        </div>

        <motion.div className="table-container" initial={{ opacity: 0 }} animate={{ opacity: 1 }}>
          <div className="table-header">
            <h2>Suspicious Transactions</h2>
            <button onClick={fetchAlerts}>üîÑ Refresh</button>
          </div>

          {loading ? (
            <p>Loading alerts...</p>
          ) : alerts.length === 0 ? (
            <p>‚úÖ No suspicious transactions right now.</p>
          ) : (
            <table>
              <thead>
                <tr>
                  <th>Account</th>
                  <th>Amount</th>
                  <th>Score</th>
                  <th>Decision</th>
                  <th>Reasons</th>
                  <th>Action</th>
                </tr>
              </thead>
              <tbody>
                {alerts.map((a) => (
                  <motion.tr key={a._id} initial={{ opacity: 0, y: 5 }} animate={{ opacity: 1, y: 0 }}>
                    <td>{a.account_id}</td>
                    <td>‚Çπ{typeof a.amount === 'number' ? a.amount.toFixed(2) : "‚Äî"}</td>
                    <td>{typeof a.score === 'number' ? `${(a.score * 100).toFixed(1)}%` : "‚Äî"}</td>
                    <td>
                      <span className={`status ${a.analyst_action || a.decision}`}>
                        {a.analyst_action ? (a.analyst_action === 'approve' ? 'Approved' : 'Blocked') : (a.decision === 'allow' ? 'Allowed' : 'Pending')}
                      </span>
                    </td>
                    <td>{Array.isArray(a.reasons) && a.reasons.length ? a.reasons.join(", ") : "‚Äî"}</td>
                    <td>
                      {a.analyst_action ? (
                        <span className="done">Action Taken</span>
                      ) : (
                        <>
                          <button className="approve" onClick={() => handleDecision(a._id, "approve")}>
                            ‚úÖ Approve
                          </button>
                          <button className="block" onClick={() => handleDecision(a._id, "block")}>
                            ‚õî Block
                          </button>
                        </>
                      )}
                    </td>
                  </motion.tr>
                ))}
              </tbody>
            </table>
          )}
        </motion.div>
      </main>
    </div>
  );
}
