import React, { useState, useEffect } from "react";
import "./App.css";
import { motion } from "framer-motion";
import { FaCheckCircle, FaTimesCircle, FaExclamationTriangle } from "react-icons/fa";

function App() {
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [metrics, setMetrics] = useState({
    total: 0,
    approved: 0,
    blocked: 0,
    pending: 0,
  });

  const BACKEND_URL = process.env.REACT_APP_BACKEND_URL || "http://localhost:3001";

  const fetchTransactions = async () => {
    setLoading(true);
    setError(null);
    try {
      const res = await fetch(`${BACKEND_URL}/api/alerts`);
      if (!res.ok) throw new Error(`Fetch failed: ${res.status}`);
      const data = await res.json();
      setTransactions(data);
      calculateMetrics(data);
    } catch (err) {
      console.error("Error fetching data:", err);
      setError(err.message || "Failed to fetch alerts");
      setTransactions([]);
      setMetrics({ total: 0, approved: 0, blocked: 0, pending: 0 });
    }
    setLoading(false);
  };

  // Map backend fields to UI states:
  // - backend `decision` is either 'alert' (needs review) or 'allow'
  // - analyst_action is set to 'approve' or 'block' after an analyst acts
  const calculateMetrics = (data) => {
    const total = data.length;
    const approved = data.filter(
      (t) => t.analyst_action === "approve" || t.decision === "allow"
    ).length;
    const blocked = data.filter((t) => t.analyst_action === "block").length;
    const pending = data.filter((t) => t.decision === "alert" && !t.analyst_action).length;

    setMetrics({ total, approved, blocked, pending });
  };

  const handleAction = async (id, action) => {
    try {
      // Optimistic UI update
      setTransactions((prev) =>
        prev.map((p) => (p._id === id ? { ...p, analyst_action: action, reviewed_at: new Date().toISOString() } : p))
      );

      const res = await fetch(`${BACKEND_URL}/api/action`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ alertId: id, action }),
      });

      if (!res.ok) {
        const errText = await res.text();
        throw new Error(`Action failed: ${res.status} ${errText}`);
      }

      // refresh metrics and list to sync with server
      fetchTransactions();
    } catch (error) {
      console.error("Error updating action:", error);
      setError(error.message || "Failed to save action");
      // revert optimistic update by re-fetching
      fetchTransactions();
    }
  };

  useEffect(() => {
    fetchTransactions();
    const interval = setInterval(fetchTransactions, 10000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="dashboard">
      <header className="header">
        <h1>AI Fraud Detection Dashboard</h1>
        <p>Real-time monitoring and risk analytics</p>
      </header>

      <section className="metrics">
        <motion.div className="card total" whileHover={{ scale: 1.05 }}>
          <h2>Total Alerts</h2>
          <p>{metrics.total}</p>
        </motion.div>
        <motion.div className="card approved" whileHover={{ scale: 1.05 }}>
          <h2>Approved</h2>
          <p>{metrics.approved}</p>
        </motion.div>
        <motion.div className="card blocked" whileHover={{ scale: 1.05 }}>
          <h2>Blocked</h2>
          <p>{metrics.blocked}</p>
        </motion.div>
        <motion.div className="card pending" whileHover={{ scale: 1.05 }}>
          <h2>Pending</h2>
          <p>{metrics.pending}</p>
        </motion.div>
      </section>

      <section className="table-section">
        <h2>Transaction Alerts</h2>
        {loading ? (
          <p className="loading">Loading data...</p>
        ) : error ? (
          <p className="error">{error}</p>
        ) : (
          <table className="transaction-table">
            <thead>
              <tr>
                <th>Account ID</th>
                <th>Amount ($)</th>
                <th>Risk Score</th>
                <th>Reason</th>
                <th>Status</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {transactions.map((t) => (
                <motion.tr
                  key={t._id}
                  initial={{ opacity: 0, y: 5 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.3 }}
                  className={
                    // Use analyst_action first (what analyst did), else map backend decision
                    t.analyst_action === "block"
                      ? "blocked-row"
                      : t.analyst_action === "approve"
                      ? "approved-row"
                      : t.decision === "allow"
                      ? "approved-row"
                      : "pending-row"
                  }
                >
                  <td>{t.account_id}</td>
                  <td>${t.amount}</td>
                  <td>{`${((t.score ?? 0) * 100).toFixed(1)}%`}</td>
                  <td>{(t.reasons && t.reasons.length) ? t.reasons.join(", ") : t.reason || "Unknown"}</td>
                  <td>
                    {t.analyst_action === "block" ? (
                      <FaTimesCircle className="icon blocked" />
                    ) : t.analyst_action === "approve" || t.decision === "allow" ? (
                      <FaCheckCircle className="icon approved" />
                    ) : (
                      <FaExclamationTriangle className="icon pending" />
                    )}
                  </td>
                  <td>
                    {t.analyst_action === "approve" || t.analyst_action === "block" || t.decision === "allow" ? (
                      <span className="done">Action Taken</span>
                    ) : (
                      <div className="btn-group">
                        <button
                          className="btn approve"
                          onClick={() => handleAction(t._id, "approve")}
                        >
                          Approve
                        </button>
                        <button
                          className="btn block"
                          onClick={() => handleAction(t._id, "block")}
                        >
                          Block
                        </button>
                      </div>
                    )}
                  </td>
                </motion.tr>
              ))}
            </tbody>
          </table>
        )}
      </section>
    </div>
  );
}

export default App;
