import React, { useEffect, useState } from "react";
import axios from "axios";
import { motion } from "framer-motion";
import {
  PieChart,
  Pie,
  Tooltip,
  Cell,
  ResponsiveContainer,
  Legend,
} from "recharts";
import toast, { Toaster } from "react-hot-toast";
import "./Dashboard.css"; // âœ… use new upgraded CSS

const COLORS = ["#facc15", "#22c55e", "#ef4444"]; // gold, green, red

export default function App() {
  const [alerts, setAlerts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [lightMode, setLightMode] = useState(false);

  const fetchAlerts = async () => {
    try {
      const res = await axios.get("http://localhost:3001/api/alerts");
      setAlerts(res.data);
    } catch (err) {
      console.error(err);
      toast.error("Failed to fetch alerts");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchAlerts();

    // Load saved theme
    const savedTheme = localStorage.getItem("theme");
    if (savedTheme === "light") setLightMode(true);
  }, []);

  const handleDecision = async (id, action) => {
    try {
      await axios.post("http://localhost:3001/api/action", {
        alertId: id,
        action,
      });
      toast.success(`Marked as ${action.toUpperCase()}`);
      fetchAlerts();
    } catch (err) {
      console.error(err);
      toast.error("Error saving action");
    }
  };

  const chartData = [
    { name: "Alerts", value: alerts.length },
    { name: "Approved", value: alerts.filter((a) => a.analyst_action === "approve").length },
    { name: "Blocked", value: alerts.filter((a) => a.analyst_action === "block").length },
  ];

  const toggleTheme = () => {
    const newTheme = !lightMode;
    setLightMode(newTheme);
    localStorage.setItem("theme", newTheme ? "light" : "dark");
  };

  return (
    <div className={`app ${lightMode ? "light" : ""}`}>
      <Toaster position="top-right" />

      {/* ðŸŒˆ Navbar */}
      <header className="navbar">
        <h1>ðŸ’³ Real-Time Fraud Detection Dashboard</h1>
        <button onClick={toggleTheme} className="mode-btn">
          {lightMode ? "ðŸŒž Light Mode" : "ðŸŒ™ Dark Mode"}
        </button>
      </header>

      {/* ðŸ§Š Dashboard Cards */}
      <main className="main">
        <div className="cards">
          <motion.div whileHover={{ scale: 1.05 }} className="card blue">
            <h3>Total Alerts</h3>
            <p>{alerts.length}</p>
          </motion.div>

          <motion.div whileHover={{ scale: 1.05 }} className="card green">
            <h3>Approved</h3>
            <p>{alerts.filter((a) => a.analyst_action === "approve").length}</p>
          </motion.div>

          <motion.div whileHover={{ scale: 1.05 }} className="card red">
            <h3>Blocked</h3>
            <p>{alerts.filter((a) => a.analyst_action === "block").length}</p>
          </motion.div>
        </div>

        {/* ðŸ“ˆ Chart */}
        <div className="chart-container">
          <h2>Fraud Overview</h2>
          <ResponsiveContainer width="100%" height={260}>
            <PieChart>
              <Pie
                dataKey="value"
                data={chartData}
                outerRadius={100}
                label
              >
                {chartData.map((entry, index) => (
                  <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                ))}
              </Pie>
              <Tooltip />
              <Legend />
            </PieChart>
          </ResponsiveContainer>
        </div>

        {/* ðŸ“‹ Table */}
        <motion.div
          className="table-container"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
        >
          <div className="table-header">
            <h2>Suspicious Transactions</h2>
            <button className="mode-btn" onClick={fetchAlerts}>ðŸ”„ Refresh</button>
          </div>

          {loading ? (
            <p>Loading alerts...</p>
          ) : alerts.length === 0 ? (
            <p>âœ… No suspicious transactions right now.</p>
          ) : (
            <table>
              <thead>
                <tr>
                  <th>Account</th>
                  <th>Amount</th>
                  <th>Score</th>
                  <th>Decision</th>
                  <th>Reasons</th>
                  <th>Action</th>
                </tr>
              </thead>
              <tbody>
                {alerts.map((a, i) => (
                  <motion.tr key={i} initial={{ opacity: 0, y: 6 }} animate={{ opacity: 1, y: 0 }}>
                    <td>{a.account_id}</td>
                    <td>â‚¹{a.amount?.toFixed(2) || "â€”"}</td>
                    <td>{a.score?.toFixed(2)}</td>
                    <td>
                      <span className={`status ${a.analyst_action || a.decision}`}>
                        {a.analyst_action || a.decision}
                      </span>
                    </td>
                    <td>{a.reasons?.join(", ") || "â€”"}</td>
                    <td>
                      <button
                        className="approve"
                        onClick={() => handleDecision(a._id, "approve")}
                      >
                        âœ… Approve
                      </button>
                      <button
                        className="block"
                        onClick={() => handleDecision(a._id, "block")}
                      >
                        â›” Block
                      </button>
                    </td>
                  </motion.tr>
                ))}
              </tbody>
            </table>
          )}
        </motion.div>
      </main>
    </div>
  );
}
