import React, { useEffect } from "react";
import "./App.css";

function App() {
  useEffect(() => {
    // Load dashboard logic dynamically
    const script = document.createElement("script");
    script.src = "/dashboard.js";
    script.async = true;
    document.body.appendChild(script);

    return () => {
      document.body.removeChild(script);
    };
  }, []);

  return (
    <div className="dashboard-container">
      <h1>Fraud Detection Dashboard</h1>

      <div className="summary">
        <div className="card">
          <h3>Total Alerts</h3>
          <p id="totalAlerts">0</p>
        </div>
        <div className="card">
          <h3>Approved</h3>
          <p id="approvedCount">0</p>
        </div>
        <div className="card">
          <h3>Blocked</h3>
          <p id="blockedCount">0</p>
        </div>
        <div className="card">
          <h3>Pending</h3>
          <p id="pendingCount">0</p>
        </div>
      </div>

      <canvas id="riskChart" width="400" height="150"></canvas>

      <div className="table-container">
        <table>
          <thead>
            <tr>
              <th>Account ID</th>
              <th>Amount</th>
              <th>Risk</th>
              <th>Decision</th>
              <th>Reason</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody id="alertsBody"></tbody>
        </table>
      </div>

      <button id="btnExport">Export CSV</button>
    </div>
  );
}

export default App;
